                               `useraddcon` varchar(30) NOT NULL DEFAULT '' COMMENT '会员发布器',
                               `usermancon` varchar(30) NOT NULL DEFAULT '' COMMENT '会员管理器',
                               `usereditcon` varchar(30) NOT NULL DEFAULT '' COMMENT '会员编辑器',
