                               `allfields` text COMMENT '所有字段',
                               `issystem` smallint(6) NOT NULL DEFAULT '0' COMMENT '是否为系统模型',
                               `isshow` smallint(6) NOT NULL DEFAULT '1' COMMENT '是否显示',
                               `issend` smallint(6) NOT NULL DEFAULT '0' COMMENT '是否支持投稿',
                               `arcsta` smallint(6) NOT NULL DEFAULT '-1',
                               `usertype` char(10) NOT NULL DEFAULT '',
                               `sendrank` smallint(6) NOT NULL DEFAULT '10',
