DROP TABLE IF EXISTS `#@__website`;
CREATE TABLE `#@__website` (
                               `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '配置ID',
                               `name` varchar(30) NOT NULL DEFAULT '' COMMENT '配置名称',
                               `type` varchar(50) NOT NULL DEFAULT '0' COMMENT '配置类型',
                               `title` varchar(50) NOT NULL DEFAULT '' COMMENT '配置标题',
                               `value` text NOT NULL COMMENT '配置值',
                               `group` varchar(50) NOT NULL DEFAULT '0' COMMENT '配置分组',
                               `extra` varchar(255) NOT NULL DEFAULT '' COMMENT '配置选项',
                               `describe` varchar(255) NOT NULL DEFAULT '' COMMENT '配置说明',
                               `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
                               `update_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
                               `visible` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
                               `sort` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
                               PRIMARY KEY (`id`),
                               UNIQUE KEY `uk_name` (`name`),
                               KEY `type` (`type`),
                               KEY `group` (`group`)
) ENGINE=MyISAM AUTO_INCREMENT=80 DEFAULT CHARSET=utf8 COMMENT='[cms]网站配置表';

